layui.define(["request", "hsForm", "hsTable"], function (exports) {
    var request = layui.request;
    var hsForm = layui.hsForm;
    var hsTable = layui.hsTable;
    var template;
    request.get(window.RESOURCE_PATH + "modules/pages/{{moduleFile}}/{{entityName}}Save.hf", function (json) {
        template = json;
    });

    function init(containerId) {
        hsTable.init("user-table" + new Date().getTime(), containerId, "{{requestMapping}}", [[
            {{each properties property i}}{{if property.name!='id'&&property.list=='true'}}
            {field: '{{property.name}}', title: "{{property.comment}}", sort: true},
            {{/if}}{{/each}}
            {
            type: "toolbar", title: "操作", toolbar: "<script type='text/html'>" +
            "<button lay-event=\"edit\" class='layui-btn layui-btn-sm'><i class=\"layui-icon\">&#xe642;</i>编辑</button>" +
            "</script>"
            }
        ]], {
            btns: [{
                name: '新建',
                callback: function () {
                    hsForm.openForm(template, function (form) {
                        console.log(form);
                        return true;
                    });
                }
            }],
            search: [
            {{each properties property i}}{{if property.name!='id'&&property.search=='true'}}
               {
                    label: '{{property.comment}}',
                    column: '{{property.name}}',
                    type: 'input'
                },
            {{/if}}{{/each}}]
        });

    }

    exports("userManage", {
        init: init
    })
});

